const API_CONFIG = {
  BASE_URL: process.env.REACT_APP_API_URL || 'http://localhost:3001',
  ENDPOINTS: {
    AUTH: {
      LOGIN: '/auth/login',
      REGISTER: '/auth/register',
      REFRESH: '/auth/refresh',
      LOGOUT: '/auth/logout'
    },
    SECURITY: {
      SCAN: '/security/scan',
      CRYPTO: {
        ENCRYPT: '/security/crypto/encrypt',
        DECRYPT: '/security/crypto/decrypt',
        HASH: '/security/crypto/hash',
        HMAC: '/security/crypto/hmac',
        ENCODE: '/security/crypto/encode',
        DECODE: '/security/crypto/decode',
        ANALYZE: '/security/crypto/analyze'
      },
      REVERSE: {
        ANALYZE_FILE: '/security/reverse/analyze-file',
        ANALYZE_CONTAINER: '/security/reverse/analyze-container',
        EXTRACT_STRINGS: '/security/reverse/extract-strings'
      }
    }
  },
  TOKENS: {
    ACCESS: 'access_token',
    REFRESH: 'refresh_token'
  }
};

export default API_CONFIG;
