import React, { useEffect, useState, useCallback } from 'react';
import { useNavigate } from 'react-router-dom';
import PropTypes from 'prop-types';
import {
  Container,
  Typography,
  Grid,
  Card,
  CardContent,
  CardHeader,
  Button,
  TextField,
  Box,
  Alert,
  CircularProgress,
  Tabs,
  Tab,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
} from '@mui/material';
import SecurityIcon from '@mui/icons-material/Security';
import WarningIcon from '@mui/icons-material/Warning';
import CheckCircleIcon from '@mui/icons-material/CheckCircle';
import axios from 'axios';
import { debounce } from 'lodash';
import { ENDPOINTS, SCAN_TYPES } from '../constants/api';
import { isTokenValid, getAuthHeader, removeToken } from '../utils/auth';

function Dashboard() {
  const navigate = useNavigate();
  const [activeTab, setActiveTab] = useState(0);
  const [scanUrl, setScanUrl] = useState('');
  const [scanType, setScanType] = useState(SCAN_TYPES.VULNERABILITY);
  const [scanning, setScanning] = useState(false);
  const [scanResults, setScanResults] = useState(null);
  const [error, setError] = useState('');
  const [monitoringId, setMonitoringId] = useState(null);
  const [monitoringResults, setMonitoringResults] = useState(null);
  const [confirmDialog, setConfirmDialog] = useState(false);
  const [pollingInterval, setPollingInterval] = useState(null);

  // Validate URL format
  const isValidUrl = (url) => {
    try {
      new URL(url);
      return true;
    } catch {
      return false;
    }
  };

  // Debounced URL validation
  const debouncedUrlValidation = useCallback(
    debounce((url) => {
      if (url && !isValidUrl(url)) {
        setError('Please enter a valid URL');
      } else {
        setError('');
      }
    }, 500),
    []
  );

  useEffect(() => {
    if (!isTokenValid()) {
      removeToken();
      navigate('/login');
    }
  }, [navigate]);

  useEffect(() => {
    debouncedUrlValidation(scanUrl);
    return () => debouncedUrlValidation.cancel();
  }, [scanUrl, debouncedUrlValidation]);

  useEffect(() => {
    return () => {
      if (pollingInterval) {
        clearInterval(pollingInterval);
      }
    };
  }, [pollingInterval]);

  const handleScan = async () => {
    if (!isValidUrl(scanUrl)) {
      setError('Please enter a valid URL');
      return;
    }

    setScanning(true);
    setError('');
    try {
      const response = await axios.post(
        ENDPOINTS.SCAN,
        { url: scanUrl, scanType },
        { headers: getAuthHeader() }
      );
      setScanResults(response.data);
    } catch (err) {
      setError(err.response?.data?.error || 'Scan failed. Please try again.');
      if (err.response?.status === 401) {
        removeToken();
        navigate('/login');
      }
    }
    setScanning(false);
  };

  const startMonitoring = async () => {
    if (!isValidUrl(scanUrl)) {
      setError('Please enter a valid URL');
      return;
    }

    try {
      const response = await axios.post(
        ENDPOINTS.MONITOR,
        { url: scanUrl },
        { headers: getAuthHeader() }
      );
      setMonitoringId(response.data.monitoringId);
      const interval = setInterval(() => pollMonitoringResults(response.data.monitoringId), 5000);
      setPollingInterval(interval);
    } catch (err) {
      setError(err.response?.data?.error || 'Failed to start monitoring');
      if (err.response?.status === 401) {
        removeToken();
        navigate('/login');
      }
    }
  };

  const stopMonitoring = () => {
    if (pollingInterval) {
      clearInterval(pollingInterval);
      setPollingInterval(null);
    }
    setMonitoringId(null);
    setMonitoringResults(null);
  };

  const pollMonitoringResults = async (id) => {
    try {
      const response = await axios.get(
        ENDPOINTS.MONITOR_STATUS(id),
        { headers: getAuthHeader() }
      );
      setMonitoringResults(response.data);
    } catch (err) {
      console.error('Failed to get monitoring results:', err);
      if (err.response?.status === 401) {
        removeToken();
        navigate('/login');
      }
    }
  };

  const handleTabChange = (event, newValue) => {
    setActiveTab(newValue);
    if (newValue !== 2 && monitoringId) {
      stopMonitoring();
    }
  };

  return (
    <Container maxWidth="lg">
      <Typography variant="h4" component="h1" gutterBottom>
        Security Dashboard
      </Typography>

      <Box sx={{ mb: 4 }}>
        <Tabs value={activeTab} onChange={handleTabChange}>
          <Tab label="Overview" />
          <Tab label="Security Scan" />
          <Tab label="Monitoring" />
        </Tabs>
      </Box>

      {activeTab === 0 && (
        <Grid container spacing={3}>
          <Grid item xs={12} md={4}>
            <Card>
              <CardHeader
                avatar={<SecurityIcon color="primary" />}
                title="Security Status"
              />
              <CardContent>
                <Typography variant="body1">
                  All systems are secure and up to date.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardHeader
                avatar={<WarningIcon color="warning" />}
                title="Active Threats"
              />
              <CardContent>
                <Typography variant="body1">
                  No active threats detected.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
          <Grid item xs={12} md={4}>
            <Card>
              <CardHeader
                avatar={<CheckCircleIcon color="success" />}
                title="System Health"
              />
              <CardContent>
                <Typography variant="body1">
                  All systems are operating normally.
                </Typography>
              </CardContent>
            </Card>
          </Grid>
        </Grid>
      )}

      {activeTab === 1 && (
        <Box>
          <Typography variant="h5" gutterBottom>
            Security Scan
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Website URL"
                value={scanUrl}
                onChange={(e) => setScanUrl(e.target.value)}
                margin="normal"
                error={!!error}
                helperText={error}
              />
            </Grid>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                select
                label="Scan Type"
                value={scanType}
                onChange={(e) => setScanType(e.target.value)}
                margin="normal"
                SelectProps={{
                  native: true,
                }}
              >
                <option value={SCAN_TYPES.VULNERABILITY}>Vulnerability Scan</option>
                <option value={SCAN_TYPES.CONFIGURATION}>Configuration Check</option>
                <option value={SCAN_TYPES.SSL}>SSL Certificate Check</option>
              </TextField>
            </Grid>
          </Grid>
          <Button
            variant="contained"
            color="primary"
            onClick={() => setConfirmDialog(true)}
            disabled={scanning || !scanUrl || !!error}
            sx={{ mt: 2 }}
          >
            {scanning ? <CircularProgress size={24} /> : 'Start Scan'}
          </Button>

          {error && (
            <Alert severity="error" sx={{ mt: 2 }}>
              {error}
            </Alert>
          )}

          {scanResults && (
            <Card sx={{ mt: 4 }}>
              <CardHeader title="Scan Results" />
              <CardContent>
                <Typography variant="h6">Score: {scanResults.results.score}</Typography>
                <Typography variant="subtitle1">Vulnerabilities:</Typography>
                <ul>
                  {scanResults.results.vulnerabilities.map((vuln, index) => (
                    <li key={index}>
                      {vuln.type} - {vuln.severity}: {vuln.description}
                    </li>
                  ))}
                </ul>
                <Typography variant="subtitle1">Recommendations:</Typography>
                <ul>
                  {scanResults.recommendations.map((rec, index) => (
                    <li key={index}>
                      {rec.issue}: {rec.recommendation}
                    </li>
                  ))}
                </ul>
              </CardContent>
            </Card>
          )}
        </Box>
      )}

      {activeTab === 2 && (
        <Box>
          <Typography variant="h5" gutterBottom>
            Threat Monitoring
          </Typography>
          <Grid container spacing={2}>
            <Grid item xs={12} md={6}>
              <TextField
                fullWidth
                label="Website URL"
                value={scanUrl}
                onChange={(e) => setScanUrl(e.target.value)}
                margin="normal"
                error={!!error}
                helperText={error}
              />
            </Grid>
          </Grid>
          <Button
            variant="contained"
            color="primary"
            onClick={startMonitoring}
            disabled={!scanUrl || !!error || monitoringId}
            sx={{ mt: 2 }}
          >
            Start Monitoring
          </Button>
          {monitoringId && (
            <Button
              variant="outlined"
              color="secondary"
              onClick={stopMonitoring}
              sx={{ mt: 2, ml: 2 }}
            >
              Stop Monitoring
            </Button>
          )}

          {monitoringResults && (
            <Card sx={{ mt: 4 }}>
              <CardHeader title="Monitoring Results" />
              <CardContent>
                <Typography variant="h6">Status: {monitoringResults.status}</Typography>
                <Typography variant="subtitle1">Threats Detected:</Typography>
                <ul>
                  {monitoringResults.threats.map((threat, index) => (
                    <li key={index}>
                      {threat.type} - {threat.severity} at {new Date(threat.timestamp).toLocaleString()}
                    </li>
                  ))}
                </ul>
                <Typography variant="subtitle1">Metrics:</Typography>
                <ul>
                  <li>Total Requests: {monitoringResults.metrics.requests}</li>
                  <li>Blocked Requests: {monitoringResults.metrics.blocked}</li>
                  <li>Suspicious Activities: {monitoringResults.metrics.suspicious}</li>
                </ul>
              </CardContent>
            </Card>
          )}
        </Box>
      )}

      <Dialog open={confirmDialog} onClose={() => setConfirmDialog(false)}>
        <DialogTitle>Confirm Scan</DialogTitle>
        <DialogContent>
          <Typography>
            Are you sure you want to scan {scanUrl}? This may take a few minutes.
          </Typography>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setConfirmDialog(false)}>Cancel</Button>
          <Button onClick={() => {
            setConfirmDialog(false);
            handleScan();
          }} color="primary">
            Start Scan
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
}

Dashboard.propTypes = {
  // Add PropTypes if needed
};

export default Dashboard;
