const API_CONFIG = {
  BASE_URL: process.env.REACT_APP_API_URL || 'http://localhost:3001',
  ENDPOINTS: {
    AUTH: {
      LOGIN: '/api/auth/login',
      REGISTER: '/api/auth/register',
      REFRESH: '/api/auth/refresh',
      LOGOUT: '/api/auth/logout'
    },
    SECURITY: {
      SCAN: '/api/security/scan',
      CRYPTO: {
        ENCRYPT: '/api/security/crypto/encrypt',
        DECRYPT: '/api/security/crypto/decrypt',
        HASH: '/api/security/crypto/hash',
        HMAC: '/api/security/crypto/hmac',
        ENCODE: '/api/security/crypto/encode',
        DECODE: '/api/security/crypto/decode',
        ANALYZE: '/api/security/crypto/analyze'
      },
      REVERSE: {
        ANALYZE_FILE: '/api/security/reverse/analyze-file',
        ANALYZE_CONTAINER: '/api/security/reverse/analyze-container',
        EXTRACT_STRINGS: '/api/security/reverse/extract-strings'
      }
    }
  },
  TOKENS: {
    ACCESS: 'access_token',
    REFRESH: 'refresh_token'
  }
};

export default API_CONFIG;
